import React from 'react';
import axios from 'axios';
import { useEffect,useState } from 'react';
import './admib.css'

export default function Card2({ title, pdf }) {
  const pdfUrl = ` http://localhost:9029/uploads/${pdf}`;  // Assuming the files are served from an 'uploads' directory
  const [alertMessage, setAlertMessage] = useState(''); // State for alert message
  const [pdfs, setPdfs] = useState([]); // Initialize state for PDFs
 // Handle deleting a PDF
 const deletePdf = async (id) => {
    
    const token = localStorage.getItem('token');
    try {
      const response = await axios.delete(`http://localhost:9029/delete-pdf/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.status === 200) {
        setAlertMessage("File deleted successfully.");
        // Update the list of PDFs by filtering out the deleted file
        setPdfs(pdfs.filter((pdf) => pdf._id !== id));
      
      } else {
        setAlertMessage("Error deleting file.");
      }
    } catch (error) {
      console.error('Error deleting PDF:', error);
      setAlertMessage("Error deleting PDF.");
    }
  };

  return (
    <div className="card">
        <div className='buttons'>
      <h5 className="card-title">{title}</h5>
      <div className='but'>
      <a href={pdfUrl} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
        View PDF
      </a>

      <button className="btn2" 
            onClick={() => deletePdf(pdf._id)}>Delete</button>
    </div>
    </div>
    </div>
  );
}
