import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Adminpanel() {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");
  const [uploadedFiles, setUploadedFiles] = useState([]);

  // Fetch list of uploaded files when the component mounts or after a successful upload
  useEffect(() => {
    const fetchUploadedFiles = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:9029/api/user/uploaded-files', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUploadedFiles(response.data);
      } catch (error) {
        console.error('Error fetching uploaded files:', error);
      }
    };

    fetchUploadedFiles();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      console.error('No file selected');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('file', file);

    const token = localStorage.getItem('token');
    if (!token) {
      console.error('Token not found');
      return;
    }

    try {
      const result = await axios.post(
        'http://localhost:9029/api/user/upload-pdf',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log('Upload successful:', result.data);
      setAlertMessage("Your file has been uploaded successfully!");  // Set success message
      setTitle("");
      setFile(null);

      // Re-fetch uploaded files to update the list
      const updatedFiles = await axios.get('http://localhost:9029/api/user/uploaded-files', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUploadedFiles(updatedFiles.data);
    } catch (error) {
      console.error('Error uploading PDF:', error);
      setAlertMessage("Error uploading PDF.");
    }
  };

  return (
    <div className='container'>
      <h1>Upload notes here</h1>

      {/* Display alert message */}
      {alertMessage && <div className="alert alert-success">{alertMessage}</div>}

      <div className="form-floating mb-3">
        <input 
          type="text" 
          className="form-control" 
          id="floatingInput" 
          placeholder="Title" 
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label htmlFor="floatingInput">Enter PDF title</label>
      </div>

      <form className="row g-3" onSubmit={onSubmit}>
        <div className="col-auto">
          <label htmlFor="fileInput">Select file for upload</label>
          <input 
            type="file" 
            className="form-control" 
            id="fileInput" 
            onChange={(e) => setFile(e.target.files[0])}
          />
        </div>
        <button type="submit" className="btn btn-primary">Upload</button>
      </form>

      {/* Display list of uploaded files */}
      <h2 className='mt-4'>Uploaded Files:</h2>
      <ul className="list-group">
        {uploadedFiles.map((file, index) => (
          <li key={index} className="list-group-item">
            {file.title} - <a href={file.url} target="_blank" rel="noopener noreferrer">View</a>
          </li>
        ))}
      </ul>
    </div>
  );
}
