import React, { useState } from 'react';
import axios from 'axios';
import './login.css';

export default function Login() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const [role, setRole] = useState('');
  const [tc, setTc] = useState(false);  // Terms and Conditions

  const handleSubmit = () => {
    axios.post('http://localhost:3008/api/user/register', { name, email, password: pass, role, tc })
      .then(result => {
        console.log(result);
        alert(`You are signed up with this email: ${email}`);
        window.scrollTo(0, 0);
      })
      .catch(err => {
        if (err.response) {
          console.log('Error during sign-up:', err.response.data);
          alert(`Error: ${err.response.data.message || 'Unknown error occurred'}`);
        } else {
          console.log('Error during sign-up:', err);
        }
      });
  };

  return (
    <div className='container'>
      <div className="row">
        <div className="col-md-8">
          <div className='form' onSubmit={handleSubmit}>
            <div className="form-floating mb-3">
              <input type="text" className="form-control" id="floatingInputName" placeholder="name" onChange={(e) => setName(e.target.value)} />
              <label htmlFor="floatingInputName">Name</label>
            </div>
            <div className="form-floating mb-3">
              <input type="email" className="form-control" id="floatingInputEmail" placeholder="name@example.com" onChange={(e) => setEmail(e.target.value)} />
              <label htmlFor="floatingInputEmail">Email address</label>
            </div>
            <div className="form-floating mb-3">
              <input type="password" className="form-control" id="floatingPassword" placeholder="Password" onChange={(e) => setPass(e.target.value)} />
              <label htmlFor="floatingPassword">Password</label>
            </div>
            <div className="form-floating mb-3">
              <input type="text" className="form-control" id="floatingRole" placeholder="enter role user/admin" onChange={(e) => setRole(e.target.value)} />
              <label htmlFor='floatingRole'>Role</label>
            </div>
            <div className="form-check">
              <input className="form-check-input" type="checkbox" value={tc} id="tcCheck" onChange={(e) => setTc(e.target.checked)} />
              <label className="form-check-label" htmlFor="tcCheck">
                I agree to the terms and conditions
              </label>
            </div>
            <div className="d-grid gap-2">
              <button className="btn btn-primary" type="button" onClick={handleSubmit}>Sign Up</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
