import React, { useState } from 'react';
import './login.css';
import axios from 'axios';

export default function Signup() {
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [nameError, setNameError] = useState('');
  const [roleError, setRoleError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [emailError, setEmailError] = useState('');
  const handleLogin = () => {
    if (validate()) {
      axios.post('http://localhost:9029/api/user/login', { email, password })
        .then(result => {
          console.log("Login successful:", result);
          // Trigger an alert for successful login
          alert(`Login successful! You are logged in as: ${result.data.name || email}`);
          // Perform any other actions such as redirecting or resetting the form
          window.scrollTo(0, 0);
        })
        .catch(err => {
          console.error("Login failed:", err);
  
          // Handle specific error responses
          if (err.response) {
            console.error("Response data:", err.response.data);
            console.error("Response status:", err.response.status);
            console.error("Response headers:", err.response.headers);
            
            // Example of specific error handling:
            if (err.response.status === 401) {
              alert("Invalid credentials. Please try again.");
            } else {
              alert(`Login failed: ${err.response.data.message || "An unknown error occurred."}`);
            }
          } else if (err.request) {
            console.error("No response received:", err.request);
            alert("No response received from the server. Please check your network connection.");
          } else {
            console.error('Error during request setup:', err.message);
            alert(`Error occurred: ${err.message}`);
          }
        });
        
      localStorage.setItem('user', JSON.stringify({ name, email, password }));
      console.log("Saved user in localStorage:", name, email, password);
    }
  };
  

  const validate = () => {
    let isValid = true;

    if (name.trim() === '') {
      setNameError('Please enter a name');
      isValid = false;
    } else {
      setNameError('');
    }

    const emailRegex = /^([a-z0-9\.-]+)@([a-z0-9]+)\.([a-z]{2,8})(\.[a-z]{2,8})?$/;
    if (email.trim() === '') {
      setEmailError('Please enter an email address');
      isValid = false;
    } else if (!emailRegex.test(email)) {
      setEmailError('Please enter a valid email address');
      isValid = false;
    } else {
      setEmailError('');
    }

    if (password.trim() === '') {
      setPasswordError('Please enter a password');
      isValid = false;
    } else if (password.length < 6) {
      setPasswordError('Password must be at least 6 characters long');
      isValid = false;
    } else {
      setPasswordError('');
    }

    if (role.trim() === '') {
      setRoleError('Please enter a role');
      isValid = false;
    } else if (!['user', 'admin'].includes(role.toLowerCase())) {
      setRoleError('Role must be either "user" or "admin"');
      isValid = false;
    } else {
      setRoleError('');
    }

    return isValid;
  };

  return (
    <div className='container' style={{ borderRadius: '20px', maxWidth: '1000px', backgroundColor: '#949EFF', padding: '40px', marginTop: '30px' }}>
      <div className="row">
        <div className="col-md-4" style={{ borderRadius: '20px', marginBottom: '20px' }}>
          <p className='p1'>Pen Down</p>
          <img className='img-fluid' src='book.png' alt='Login' />
        </div>
        <div className="col-md-8" style={{ borderRadius: '20px', backgroundColor: 'white', padding: '30px' }}>
          <p className='p2'>Login</p>
          <div className="form-floating mb-3">
            <input
              type="email"
              className="form-control"
              id="floatingInputName"
              placeholder="username"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label htmlFor="floatingInputName">Email</label>
            {emailError && <div className="text-danger">{emailError}</div>}
          </div>

          <div className="form-floating mb-3">
            <input
              type="password"
              className="form-control"
              id="floatingPassword"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <label htmlFor="floatingPassword">Password</label>
            {passwordError && <div className="text-danger">{passwordError}</div>}
          </div>

          <div className="d-grid gap-2">
            <button className="btn btn-primary" type="button" onClick={handleLogin} style={{ backgroundColor: '#949EFF', border: 'none' }}>Login</button>
          </div>
          <br />
          <div className="d-flex align-items-center">
            <span style={{ fontSize: '10px' }}>New user</span>
            <button className="btn" type="button" style={{ backgroundColor: 'white', border: 'none', color: '#949EFF', fontSize: '10px' }}>Sign Up</button>
            <button className="btn" type="button" style={{ backgroundColor: 'white', border: 'none', color: '#949EFF', marginLeft: '280px' }}>Forgot password</button>
          </div>
        </div>
      </div>
    </div>
  );
}
