import React, { useState } from 'react';
import axios from 'axios';

export default function Adminpanel() {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);  // Initialize as null instead of an empty string

  const onSubmit = async (e) => {
    e.preventDefault();

    // Create form data
    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);

    // Retrieve the token from localStorage
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('Token not found');
      return; // Exit if no token
    }
    

    try {
      const result = await axios.post("http://localhost:9029/api/user/upload-pdf", formData, {
        headers: {
          'Authorization': `Bearer ${token}`,  // Send the token with the request
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log(result.data);  // Handle the response appropriately
    } catch (error) {
      console.error('Error uploading PDF:', error);
    }
  };

  return (
    <div className='container'>
      <h1>Upload notes here</h1>

      <div className="form-floating mb-3">
        <input 
          type="text" 
          className="form-control" 
          id="floatingInput" 
          placeholder="Title" 
          value={title}  // Controlled input for title
          onChange={(e) => setTitle(e.target.value)}
        />
        <label htmlFor="floatingInput">Enter PDF title</label>
      </div>

      <form className="row g-3" onSubmit={onSubmit}>  {/* Attach onSubmit to form */}
        <div className="col-auto">
          <label htmlFor="fileInput">Select file for upload</label>
          <input 
            type="file" 
            className="form-control" 
            id="fileInput" 
            onChange={(e) => setFile(e.target.files[0])}  // Correct way to handle file input
          />
        </div>
        <button type="submit" className="btn btn-primary">Upload</button>  {/* Change to submit */}
      </form>
    </div>
  );
}
