import React from 'react';
import { useState } from 'react';
import 'axios'
import axios from 'axios';



export default function Adminpanel() {
const[title,setTitle]=useState("");
const[file,setFile]=useState("");
   
const onsubmit= async(e)=>{
  e.preventDefault();
  const formdata = new FormData();
  formdata.append("title", title);
  formdata.append("file", file);

  const token = localStorage.getItem('token'); // Retrieve the token from localStorage or another state management system

  try {
    const resut = await axios.post("http://localhost:9029/api/user/${token}upload-pdf", formdata, {
      headers: {
        'Authorization': `Bearer ${token}`, // Send the token with the request
        'Content-Type': 'multipart/form-data'
      }
    });
    const token = localStorage.getItem('token');
if (!token) {
  console.error('Token not found');
  return;  // Stop form submission if there's no token
}
    console.log(resut);
  } catch (error) {
    console.error('Error uploading PDF:', error);
  }
};



  return (
    <div className='container'>
      <h1>Upload notes here</h1>
      
      <div className="form-floating mb-3">
        <input 
          type="text" 
          className="form-control" 
          id="floatingInput" 
          placeholder="title" 
          onChange={(e)=>setTitle(e.target.value)}
        />
        <label htmlFor="floatingInput">Enter pdf title</label>
      </div>
      
      <form className="row g-3">
        <div className="col-auto">
          <label htmlFor="fileInput">Select file for upload</label>
          <input 
            type="file" 
            className="form-control" 
            id="fileInput" 
            onChange={(e)=>setFile(e.target.files[0])}
          />
        </div>
        <button type="button" className="btn btn-primary" onClick={onsubmit}>upload</button>
        
      </form>
    </div>
  );
}
