import React from 'react';


export default function Card({ title, pdf }) {
  const pdfUrl = `http://localhost:9001/uploads/${pdf}`;  // Assuming the files are served from an 'uploads' directory

  return (
    <div className="card">
      <h5 className="card-title">{title}</h5>
      <a href={pdfUrl} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
        View PDF
      </a>
    </div>
  );
}
