import React from 'react';
import './card.css'

export default function Card({ title, pdf }) {
  const pdfUrl = ` http://localhost:9029/uploads/${pdf}`;  // Assuming the files are served from an 'uploads' directory
  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = pdfUrl;
    link.download = `${title}.pdf`; // Set the download filename
    link.click();
  };
  return (
    <div className='row'>
       <div className="card-header">
      <h5 className="card-title">{title}</h5>

      <div className='button-container'>
      <a href={pdfUrl} target="_blank" rel="noopener noreferrer" className="btn2" onClick={handleDownload}>
        View 
      </a>
      
      
      <button className="btn1">
        download
      </button>
      </div>
     

    </div>
    </div>
   
  );
}
