import React from 'react';
import './login.css';
import { useEffect,useState } from 'react';

export default function Login() {
  const [nameError , setnameError] = useState('');
  const [passwardError , setpasswardError] = useState('');
  const [emailError , setemailError] = useState('');
  const [roleError , setroleError] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const [role, setrole] = useState('');

  const handleSubmit = () => {
    axios.post('http://localhost:3008/api/user/register', { name, email, password: pass ,role})
      .then(result => {
        console.log(result);
        alert(`You are signed up with this email: ${email}`);
        window.scrollTo(0, 0);
        setIsLogin(true); // Switch back to login form after successful signup
      })
      .catch(err => console.log(err));
  };
  const validate = (event) => {
    event.preventDefault(); // Prevent default form submission

    const user = document.getElementById('username').value;
    const pass = document.getElementById('password').value;
    const email = document.getElementById('email') ? document.getElementById('email').value : '';

    // Regular expression for email validation
    const emailRegex = /^([a-z0-9\.-]+)@([a-z0-9]+)\.([a-z]{2,8})(\.[a-z]{2,8})?$/;

    setUsernameError('');
    setPasswordError('');
    setEmailError('');

    if (user.trim() === '') {
      setnameError('Please enter a username');
      return false;
    }

    if (pass.trim() === '') {
    setpasswardError('Please enter a password');
      return false;
    }

    if (!isLogin && !emailRegex.test(email)) {
      setemailError('Please enter a valid email address');
      return false;
    }

    if (pass.length < 6) {
      setemailError('Password must be at least 6 characters long');
      return false;
    }

    // All validations passed
    console.log('Form submitted');

    if (isLogin) {
      handleLogin();
    } else {
      handleSubmit();
    }

    return true;
  };
  
  return (
    <div className='container' style={{ borderRadius: '20px', maxWidth: '1000px', backgroundColor: '#949EFF' ,padding:'40px', marginTop:'30px'}}>
      <div className="row">
        <div className="col-md-4" style={{ borderRadius: '20px', marginBottom: '20px' }}>
          <p className='p1'>Welcome Users</p>
          <img className='img-fluid' src='login.png' alt='Login'></img>
        </div>
        <div className="col-md-8" style={{ borderRadius: '20px', backgroundColor: 'white', padding:'30px' }}>
          <p className='p2'>Sign up</p>
          <div className='form' onSubmit={validate}>
          <div className="form-floating mb-3">
            <input type="text" className="form-control" id="floatingInputName" placeholder="name" onChange={(e) => setName(e.target.value)}/>
            <label htmlFor="floatingInputName">Name</label>
          </div>
          <div className="form-floating mb-3">
            <input type="email" className="form-control" id="floatingInputEmail" placeholder="name@example.com" />
            <label htmlFor="floatingInputEmail">Email address</label>
          </div>
          <div className="form-floating mb-3">
            <input type="password" className="form-control" id="floatingPassword" placeholder="Password" />
            <label htmlFor="floatingPassword">Password</label>
          </div>
          <div className='row mb-3'>
            <div className='col'>
              {/* <div className="form-check">
                <input className="form-check-input" type="checkbox" value="" id="flexCheckAdmin" />
                <label className="form-check-label" htmlFor="flexCheckAdmin">
                  Sign in as admin
                </label>
              </div> */}
              <div className="form-floating mb-3">
            <input type="text" className="form-control" id="floatingPassword" placeholder="enter role user/admin" />
            <label htmlFor='floatingInputRole'>Role</label>
          </div>
            </div>
            {/* <div className='col'>
              <div className="form-check">
                <input className="form-check-input" type="checkbox" value="" id="flexCheckUser" />
                <label className="form-check-label" htmlFor="flexCheckUser">
                  Sign in as user
                </label>
              </div>
            </div> */}
          </div>
          <div className="d-grid gap-2">
            <button className="btn btn-primary" type="button" style={{backgroundColor: '#949EFF', border:'none'}}>Sign Up</button>
          </div>
          
</div>
        </div>
      </div>
    </div>
  );
}
