import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Adminpanel() {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);
  const [alertMessage, setAlertMessage] = useState("");
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [pdfs, setPdfs] = useState([]);  // List of PDFs fetched from the backend

  // Fetch list of uploaded PDFs when the component mounts or after a successful upload
  useEffect(() => {
    const fetchPdfs = async () => {
      try {
        const response = await axios.get('http://localhost:9029/api/user/uploaded-files');
        
        if (response.status === 200) {
          setUploadedFiles(response.data);  // Assuming the response is an array of files
        } else {
          console.error('Failed to fetch files:', response);
        }
      } catch (error) {
        console.error('Error fetching uploaded files:', error);
      }
    };

    fetchPdfs();
  }, []);  // Runs once when the component mounts

  // Handle form submission for file upload
  const onSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setAlertMessage("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('file', file);

    const token = localStorage.getItem('token');
    if (!token) {
      setAlertMessage("Token not found. Please log in.");
      return;
    }

    try {
      const result = await axios.post(
        'http://localhost:9029/api/user/upload-pdf',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log('Upload successful:', result.data);
      setAlertMessage("Your file has been uploaded successfully!");  // Set success message
      setTitle("");
      setFile(null);

      // Re-fetch uploaded files to update the list after a successful upload
      const updatedFiles = await axios.get('http://localhost:9029/api/user/pdf', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUploadedFiles(updatedFiles.data);
    } catch (error) {
      console.error('Error uploading PDF:', error);
      setAlertMessage("Error uploading PDF.");
    }
  };

  return (
    <div className='container'>
      <h1>Upload Notes Here</h1>

      {/* Display alert message */}
      {alertMessage && <div className="alert alert-success">{alertMessage}</div>}

      <div className="form-floating mb-3">
        <input 
          type="text" 
          className="form-control" 
          id="floatingInput" 
          placeholder="Title" 
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label htmlFor="floatingInput">Enter PDF title</label>
      </div>

      <form className="row g-3" onSubmit={onSubmit}>
        <div className="col-auto">
          <label htmlFor="fileInput">Select file for upload</label>
          <input 
            type="file" 
            className="form-control" 
            id="fileInput" 
            onChange={(e) => setFile(e.target.files[0])}
          />
        </div>
        <button type="submit" className="btn btn-primary">Upload</button>
      </form>

      {/* Display list of uploaded files */}
      <h2 className='mt-4'>Uploaded Files:</h2>
      {uploadedFiles.length > 0 ? (
        <ul className="list-group">
          {uploadedFiles.map((file, index) => (
            <li key={index} className="list-group-item">
              {file.title} - <a href={`http://localhost:9029/uploads/${file.pdf}`} target="_blank" rel="noopener noreferrer">View</a>
            </li>
          ))}
        </ul>
      ) : (
        <p>No files uploaded yet.</p>
      )}
    </div>
  );
}
