import React, { useEffect, useState } from 'react';
import './user.css';
import Card from './card';
import Profile from './Profile';

export default function Userpanel() {
  const [pdfs, setPdfs] = useState([]);

  // Fetch PDF data from the backend
  useEffect(() => {
    const fetchPdfs = async () => {
      try {
       router.get('/pdf', fetchPdfs);
        
        // Check if the response is valid and in JSON format
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
  
        const data = await response.json();
        console.log(data);
        
        if (data.status === 'success') {
          setPdfs(data.data); // Assuming the PDFs are stored in 'data.data'
        } else {
          console.error('Error: Unexpected API response structure.');
        }
      } catch (error) {
        console.error('Error fetching PDF data:', error);
      }
    };
  
    fetchPdfs();
  }, []);
  

  return (
    <div className='con'>
      <div className="row">
        <div className="col-4">
          {/* Sidebar or any content for the left column */}
        </div>
        <div className="col-8">
          <div className='Panel'>
            <br />
            <nav className="navbar ">
              <div className="container-fluid">
                <form className="d-flex">
                  <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                  <button className="btn" type="submit">Search</button>
                </form>
                <Profile />
              </div>
            </nav>
            <br />
            <br />

            {/* Render Cards for each PDF */}
            {pdfs.map((pdf, index) => (
              <React.Fragment key={index}>
                <Card title={pdf.title} pdf={pdf.pdf} />
                <br />
              </React.Fragment>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
