import React from 'react';
import './auth.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFacebook, faInstagram, faLinkedin, faAcademia } from '@fortawesome/free-brands-svg-icons';
import { faEnvelope } from '@fortawesome/free-solid-svg-icons';

export default function Author() {
  return (
    <div className='container2'>
      <h1 className='head'>About Author</h1>
      <div className="row">
        <div className="col">
          <div className='contain'>
            <img src='./Author.jpg' className='im1' alt='Author' />
          </div>
        </div>
        <div className="col">
          <div className='container'>
            <p>
              <span>Dr. Renu Malra</span>, Assistant Professor in the Dept. of Tourism, Institute of Integrated and Honors Studies, Kurukshetra University, since 2006. She has authored many books on tourism fundamentals and environmental impacts of tourism, published articles in reputed journals, and her research on topics such as environmental impacts of tourism, COVID-19 effects, Blockchain in tourism, and implications of the metaverse in tourism are setting milestones.
              <br />
              Her areas of interest include E-Tourism, Entrepreneurship, Sustainable Tourism, and Tourism Marketing.
            </p>
          </div>
        </div>
      </div>

      <div className="row social-icons">
        {/* Facebook */}
        <a href="https://www.facebook.com/author-profile" target="_blank" rel="noopener noreferrer">
          <FontAwesomeIcon icon={faFacebook} style={{ color: "#1877e0" }} />
        </a>

        {/* Instagram */}
        <a href="https://www.instagram.com/author-profile" target="_blank" rel="noopener noreferrer">
          <FontAwesomeIcon icon={faInstagram} style={{ color: "#E1306C" }} />
        </a>

        {/* Gmail */}
        <a href="mailto:author.email@gmail.com" target="_blank" rel="noopener noreferrer">
          <FontAwesomeIcon icon={faEnvelope} style={{ color: "#D44638" }} />
        </a>

        {/* LinkedIn */}
        <a href="https://www.linkedin.com/in/author-profile" target="_blank" rel="noopener noreferrer">
          <FontAwesomeIcon icon={faLinkedin} style={{ color: "#0077b5" }} />
        </a>

        {/* Academia.edu */}
        <a href="https://www.academia.edu/author-profile" target="_blank" rel="noopener noreferrer">
          <FontAwesomeIcon icon={faAcademia} style={{ color: "#41454A" }} />
        </a>
      </div>
    </div>
  );
}
