import React, { useState } from 'react';
import axios from 'axios';

export default function Adminpanel() {
  const [title, setTitle] = useState("");
  const [file, setFile] = useState(null);  // Initialize as null instead of an empty string

  const onSubmit = async (e) => {
    e.preventDefault();
  
    const formData = new FormData();
    formData.append("title", title);
    formData.append("file", file);
  
    const token = localStorage.getItem('token');
  
    // Check if token exists
    if (!token) {
      console.error('Token not found');
      return;
    }
  
    console.log('Token:', token);  // Log the token to ensure it's present
  
    try {
      const result = await axios.post(`http://localhost:9029/api/user/upload-pdf/${token}`, formData, {  // Use backticks here
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}` 
        }
      });
      console.log('Upload successful:', result.data);
    } catch (error) {
      console.error('Error uploading PDF:', error);  // Log error details
    }
  };
  
  

  return (
    <div className='container'>
      <h1>Upload notes here</h1>

      <div className="form-floating mb-3">
        <input 
          type="text" 
          className="form-control" 
          id="floatingInput" 
          placeholder="Title" 
          value={title}  // Controlled input for title
          onChange={(e) => setTitle(e.target.value)}
        />
        <label htmlFor="floatingInput">Enter PDF title</label>
      </div>

      <form className="row g-3" onSubmit={onSubmit}>  {/* Attach onSubmit to form */}
        <div className="col-auto">
          <label htmlFor="fileInput">Select file for upload</label>
          <input 
            type="file" 
            className="form-control" 
            id="fileInput" 
            onChange={(e) => setFile(e.target.files[0])}  // Correct way to handle file input
          />
        </div>
        <button type="submit" className="btn btn-primary">Upload</button>  {/* Change to submit */}
      </form>
    </div>
  );
}
